mapper.insert:
insert into sys_role (role_id, rolename, deleted
  )
values (#{roleId,jdbcType=INTEGER}, #{rolename,jdbcType=VARCHAR}, #{deleted,jdbcType=TINYINT}
  )

mapper.update:
update sys_role
set rolename = #{rolename,jdbcType=VARCHAR},
  deleted = #{deleted,jdbcType=TINYINT}
where role_id = #{roleId,jdbcType=INTEGER}

mapper.select:
select DISTINCT(s_p.permission) from sys_permission s_p where s_p.role_id in (
select s_r.role_id from sys_role s_r LEFT JOIN sys_user_role s_u_r on s_r.role_id = s_u_r.role_id where s_u_r.user_id = #{userId} and s_r.deleted = 0
)
